onClick:
{{
  (() => {
    const itemId = TransItemDropdown.selectedOptionValue;
    const type = TransTypeDropdown.selectedOptionValue;
    const qty = TransQtyInput.text;

    if (!itemId || !type || qty === "" || isNaN(qty) || parseInt(qty) <= 0) {
      showAlert("Fill all fields correctly", "warning");
      return;
    }

    insertTransaction.run(
      () => {
        adjustStock.run(
          () => {
            getAllItems.run();
            showAlert("Transaction successful", "success");
          },
          (err) => showAlert("Stock update failed: " + err.message, "error")
        );
      },
      (err) => showAlert("Transaction failed: " + err.message, "error")
    );
  })()
}}
