onClick:
{{ 
(() => {
  const role = AddRoleDropdown.selectedOptionLabel;
  const email = AddEmailInput.text.trim().toLowerCase();

  const patternMatch = 
    (role === "Store Clerk" && email.endsWith("-clerk001@school.com")) ||
    (role === "Administrator" && email.endsWith("-admin001@school.com")) ||
    (role === "Inventory Manager" && email.endsWith("-manager001@school.com")) ||
    (role === "Finance Officer" && email.endsWith("-finance001@school.com"));

  if (!AddNameInput.text || !email || !AddPasswordInput.text || !AddRoleDropdown.selectedOptionValue) {
    showAlert("Please fill in all fields.", "warning");
  } else if (!patternMatch) {
    showAlert("Email format does not match selected role", "warning");
  } else {
    checkEmailExists.run(() => {
      const exists = checkEmailExists.data[0]?.count > 0;

      if (exists) {
        showAlert("Email already exists. Try another.", "warning");
      } else {
        createUser.run(
          () => {
            getAllUsers.run();
            closeModal('AddUserModal');
            showAlert('User created successfully!', 'success');
          },
          (err) => {
            showAlert("Error: " + err.message, "error");
          }
        );
      }
    });
  }
})()
}}
